import React, { Component } from 'react';
import 'materialize-css/dist/css/materialize.min.css';
import M from "materialize-css";

//import EditBook from '../Components/EditBook'
import DelBook from '../Components/DelBook'

import pointingToLeft from '../Asset/Icons/pointing-to-left.svg'
import editIcon from '../Asset/Icons/edit-icon.svg';
import delIcon from '../Asset/Icons/delete-icon.svg';

// import coverHujan from '../Asset/bookCover/img-6hujan.jpg';

import BookData from '../Asset/dataDumy/BookData';

class BookDetail extends Component{

    constructor(){
      super();
      this.state={
        book: BookData[0],
      }
    }

    componentDidMount() {
        M.AutoInit()

      const {idbook} = this.props.match.params

      this.setState({
        book: BookData[idbook]
      })
    }

    render(){
      let btnStatus=''
      if (this.state.book.status !== 'available'){
          btnStatus= 'disabled'
      }
      console.log(this.state)
      console.log('disini isi fulldesc: ',this.state.title)
        return(
            <>
        <div>
            <div style={{
                zIndex:9,
                position:'fixed',
                margin:'0 auto',
            }} >
            <div className="row">

          <a href="http://localhost:3000/" className="waves-effect waves-light" style={{
            marginLeft:'3rem',
            marginTop:'30px'
          }} >
             <img  src={pointingToLeft} style={{width: '40px'}} alt="arrow pointing left icon"/></a>

  {/* start sytem edit  */}

        <div id="modal2" className="modal" style={{
                borderRadius:'10px'
              }} >

              <div className="row" style={{
                marginTop:'1rem',
                marginBottom: '-4rem'
              }} >
                <div className="col s6">
                <h4><span style={{
                  fontWeight:'bold',
                  marginLeft:'40px'
                }} >Edit Data</span></h4>
                </div>
                <div className="col s6 right-align">
              <a href="#!"><i className="modal-close material-icons prefix red-text">close</i></a>
                </div>
              </div>
              <div className="row">
              <form className="col s12">
                <div className="row">
                  <div className="input-field col s12">
                    <i className="material-icons prefix">link</i>
                    <textarea  id="icon_prefix2" className="materialize-textarea validate" defaultValue={this.state.book.url} />
                    <label htmlFor="icon_prefix2">Url image</label>
                  </div>

                  <div className="input-field col s12">
                    <i className="material-icons prefix">short_text</i>
                    <textarea id="icon_prefix3" className="materialize-textarea" defaultValue={this.state.book.title} />
                    <label htmlFor="icon_prefix3">Title</label>
                  </div>

                  <div className="input-field col s12">
                    <i className="material-icons prefix">list</i>
                    <textarea id="icon_prefix4" className="materialize-textarea" defaultValue={this.state.tinydesc} />
                    <label htmlFor="icon_prefix4">Description</label>
                  </div>
                </div>

                <button className="btn waves-effect waves-light" type="submit"  style={{
                    marginLeft:'90%',
                    backgroundColor:'#f1c40f',
                    color:'#2c3e50',
                    marginTop:'-2rem'
                }} >Save
                </button>
              </form>
            </div>
            </div>

  {/* end system edit */}


          <a href="#modal2" className="waves-effect waves-light modal-trigger" style={{
            marginLeft:'65rem',
            marginTop:'30px'
          }} >
             <img  src={editIcon} style={{
               width: '90px',
               }} alt="this use to edit system"/></a>

  <DelBook/>
          <a href="#modal3" className="waves-effect waves-light modal-trigger" style={{
            marginLeft:'1rem',
            marginTop:'30px',
          }} >
             <img  src={delIcon} style={{width: '90px'}} alt="this use to edit system"/></a>


        </div>
            </div>

        <div className="parallax-container">
          <div className="parallax"><img src={this.state.book.img} alt="gambar dari paralax satu"/></div>
        </div>
        <div className="section white">
          <div className="row container">

              <div className="col s8">

              <div className="row">
              <div className="col s8">
                  <span className="new badge" data-badge-caption="" style={{
                    color:'black',
                    fontWeight:'bold',
                    backgroundColor:'#f1c40f',
                    marginRight:'85%'
                  }} >{this.state.book.category}</span>

                  <h2 className="header">{this.state.book.title}</h2>
                  <p>12 April 2019</p>
              </div>
              <div className="col s4">
              <h5 className="right-align" style={{
                color:'#f39c12',
                fontWeight:'bold',
                marginTop:'3rem',
              }} >{this.state.book.status}</h5>
              </div>
              </div>



              <p className="grey-text text-darken-3 lighten-3 "style={{
                textAlign:'justify'
              }} >{this.state.book.fulldesc}</p>


              </div>
              <div className="col s4">

              <img className="z-depth-3" src={this.state.book.icon} style={{
                width: '135px',
                borderRadius:'10px',
                marginLeft:'78%',
                marginTop:'-40%',
              }} alt="badge book icon"/>

              <button className={`btn ${btnStatus} waves-effect waves-light z-depth-2`}
              type="submit" style={{
              marginLeft:'90%',
              backgroundColor:'#f1c40f',
              color:'#2c3e50',
              marginTop:'70%'
              }} >Borrow
              </button>
              </div>



          </div>
        </div>
      </div>

            </>
        );
    }
}
export default BookDetail;
