import Axios from 'axios';

export const getBook = () => {
    return {
        type: 'GET_BOOK',
        payload: Axios.get('http://localhost:7777/api/v1/books')
    };
};

export const insertBook = () => {
    return {
        type: 'INSERT_BOOK',
        payload: Axios.post('localhost:7777/api/v1/books')
    };
};

export const updateBook = (id) => {
    return{
        type: 'UPDATE_BOOK',
        payload: Axios.patch(`localhost:8000/api/v1/books/book?id=${id}`)
    }
}
