import React, { Component } from 'react';
import 'materialize-css/dist/css/materialize.min.css';
import book from '../Asset/Icons/book.svg'
import M from "materialize-css";

import TheCard from '../Components/TheCard'
import MyCarousel from '../Components/MyCarousel'
import AddBook from '../Components/AddBook'
import Drawer from '../Components/Drawer';

import BookData from '../Asset/dataDumy/BookData';

class Home extends Component {
    constructor(props){
        super(props);
        this.state={
            books: BookData,
            tempBook: {
                title:'',
                author:'',
                year:'',
                category:'',
                status:'',
                icon:'',
                img:'',
                tinydesc:'',
                fulldesc:'',
            }
        };
    this.onClick = this.onClick.bind(this);
    }

    componentDidMount() {
        M.AutoInit()

        if (this.props.location.state) {
            const { newBook, isEdit, isDelete } = this.props.location.state;

            if (isEdit) {
              this.setState({
                book: newBook
              });
            }
            if (isDelete) {
              this.setState({
                book: newBook
              });
            }
        }

    }



    handleChange = event => {
        const { name, value } = event.target;

        this.setState({
            tempBook: { ...this.state.tempBook, [name]: value }
        });
    };

    onClick = e => {
        e.preventDefault();
        const {
          title,
          author,
          image_url,
          date,
          year,
          description,
          status
        } = this.state.tempBook;

        const newNovel = {
            title,
            author,
            image_url,
            date,
            year,
            description,
            status
          };

        this.setState({
        book: [...this.state.book, newNovel]
        });

        console.log('onclick');
        console.log(this.state.book);

    };

    render() {
        console.log('INI isi this.state.book: ', this.state.books);
        console.log('INI isi this.state.tempBook: ', this.state.tempBook);

        return (
            <div>
            <nav style={{
                backgroundColor:'#f5f5f5',
                position:'fixed',
                zIndex:9,
            }}> 
            <a href="#!"  className="">
            <i data-target="slide-out" 
            className="material-icons sidenav-trigger black-text">menu</i></a>

            <div className="nav-wrapper container">
                <ul className="left">
                    <li>
                        {/* Dropdown Trigger */}
                        <a className="dropdown-trigger" 
                        href="http://localhost:3000/" 
                        data-target="dropdown1"
                        ><span style={{
                            color:'#2c3e50'
                        }}>All Categories</span>
                    </a>
                        {/* Dropdown Structure */}
                        <ul id="dropdown1" className="dropdown-content">
                            <li><a href="http://localhost:3000/">Fantasy</a></li>
                            <li><a href="http://localhost:3000/">Medicine</a></li>
                            <li><a href="http://localhost:3000/">History</a></li>
                            <li><a href="http://localhost:3000/">Religious</a></li>
                            <li><a href="http://localhost:3000/">Biography</a></li>
                        </ul>
                    </li>
                    <li><i className="material-icons prefix black-text">arrow_drop_down</i></li>

                    <li>
                        {/* Dropdown Trigger */}
                        <a className="dropdown-trigger" 
                        href="http://localhost:3000/" 
                        data-target="dropdown2"
                        ><span style={{
                            color:'#2c3e50'
                        }}>All Times</span>
                    </a>
                        {/* Dropdown Structure */}
                        <ul id="dropdown2" className="dropdown-content">
                            <li><a href="#!">2019</a></li>
                            <li><a href="#!">2018</a></li>
                            <li><a href="#!">2017</a></li>
                            <li><a href="#!">2016</a></li>
                            <li><a href="#!">2015</a></li>
                        </ul>
                    </li>
                    <li><i className="material-icons prefix black-text">arrow_drop_down</i></li>


                    <li><i className="material-icons prefix black-text" htmlFor="icon_search"
                        style={{ 
                            paddingLeft: '3rem',
                             }}>search</i></li>
                    <li><input placeholder="Search book" id="icon_search" type="search" className="validate" /></li>


                </ul>

                <ul id="nav-mobile" className="right hide-on-med-and-down">
                    <li><img  src={book} style={{
                        width: '30px',
                        paddingTop: '1rem',
                        marginRight: '1rem',
                        }} alt="my book icon"/></li>
                    <li><span style={{
                        fontWeight:'bold',
                        color:'#2c3e50',
                        fontSize:'30px'
                    }}>Library</span></li>

                </ul>
            </div>
            </nav>
            <Drawer/>
            <AddBook
                title={this.state.tempBook.title}
                author={this.state.tempBook.author}
                year={this.state.tempBook.year}
                category={this.state.tempBook.category}
                status={this.state.tempBook.status}
                icon={this.state.tempBook.icon}
                img={this.state.tempBook.img}
                tinydesc={this.state.tempBook.tinydesc}
                fulldesc={this.state.tempBook.fulldesc}

                onChange={this.handleChange}
                onSubmit={this.onClick}
            />

           <div className="carousel">
            {
                this.state.books.map((book, index)=>{
                    return(
                        <MyCarousel
                            alt={book.title.trim()}
                            key={index}
                            author={book.author}
                            cardTitle={book.title}
                            imageUrl={book.img}
                        />
                    );
                })
            }

           </div>

            <div className="container">
                <div className="row">
                    {
                       this.state.books.map((book, index)=>{
                           return(
                               <TheCard
                                alt={book.title.trim()}
                                key={index}
                                bookTitle={book.title}
                                image={book.img}
                                descript={book.tinydesc}

                               />
                           );
                       })
                    }
                </div>
            </div>
            </div>


        )
    }
}

export default Home;
