import React from 'react'

const AddBook = (props) =>{


    return(
        <div id="modal1" className="modal" style={{
          borderRadius:'10px'
        }} >

        <div className="row" style={{
          marginTop:'1rem',
          marginBottom: '-4rem'
        }} >
          <div className="col s6">
          <h4><span style={{
            fontWeight:'bold',
            marginLeft:'40px'
          }} >Add Data</span></h4>
          </div>
          <div className="col s6 right-align">
         <a href="#!"><i className="modal-close material-icons prefix red-text">close</i></a>
          </div>
        </div>
        <div className="row">
        <form onSubmit={props.onSubmit} className="col s12">
          <div className="row">
            <div  className="input-field col s12">
              <i className="material-icons prefix">link</i>
              <input name='url-image' type='text' value={props.img} onChange={props.onChange} 
               id="url-image" className="materialize-textarea validate" defaultValue={""} />
              <label htmlFor="icon_prefix2">Url image</label>
            </div>

            <div className="input-field col s12">
              <i className="material-icons prefix">short_text</i>
              <input name='title' type='text' value={props.title} onChange={props.onChange} 
              id="title" className="materialize-textarea" defaultValue={""} />
              <label htmlFor="icon_prefix3">Title</label>
            </div>

            <div className="input-field col s12">
              <i className="material-icons prefix">list</i>
              <input name='tinydesc' type='text' value={props.tinydesc} onChange={props.onChange} 
              id="tinydesc" className="materialize-textarea" defaultValue={""} />
              <label htmlFor="icon_prefix4">Description</label>
            </div>
          </div>

          <  button className="btn waves-effect waves-light" type="submit" name="" style={{
              marginLeft:'90%',
              backgroundColor:'#f1c40f',
              color:'#2c3e50',
              marginTop:'-2rem'
          }} >Save
          </button>
        </form>
      </div>
      </div>
    );
}
export default AddBook;
