const initialState = {
    bookData: [],
    isLoading: false,
    isRejected: false,
    isFulfilled: false,
  };

  const books = (prevState = initialState, action) => {
    switch (action.type) {
      case 'GET_BOOK_PENDING':
        return {
          ...prevState,
          isFulfilled: false,
          isRejected: false,
          isLoading: true,
        };
      case 'GET_BOOK_REJECTED':
        return {
          ...prevState,
          isLoading: false,
          isRejected: true,
        };
      case 'GET_BOOK_FULFILLED':
        return {
          ...prevState,
          isLoading: false,
          isFulfilled: true,
          bookData: action,
        };
      default:
        return prevState;
    }
  };
  //console.log('bookData: ', initialState.bookData)
  export default books;
