import React from 'react'
import {Link} from 'react-router-dom'

const MyCard = (props) => {
    return(
        <div className="col s12 m4">
        <div className="card hoverable" style={{

            borderRadius: '20px',
            marginTop: '2rem',
            width: '100%',
            border:'solid white 10px'

        }}>
            <div className="card-image">
                <img src={props.image} 
                alt="terletak gambar untuk cover"
                style={{
                    height:'100px',
                    borderRadius: '20px  20px 0 0',
                }} />
            </div>
            <div className="card-content" style={{
                backgroundColor:'#eee',
                borderRadius: '0 0 20px  20px',
                height:'50%'

            }}>
            <Link to={props.to}>
                <span className="card-title center-align" style={{
                    fontWeight:'bold',
                    color:'#eb4d4b'
                }}>{props.bookTitle}</span>
            </Link>

                <p style={{
                    fontSize:'13px',
                    textAlign:'center'
                }} >{props.descript}</p>
            </div>
        </div>
    </div> 
    );
}
export default MyCard;
