import React, { Component } from 'react';
import { connect } from 'react-redux';
import {getBooks, insertBook} from '../Public/Redux/Actions/books' 

import 'materialize-css/dist/css/materialize.min.css';
import book from '../Asset/Icons/book.svg'
import M from "materialize-css";

import TheCard from '../Components/TheCard'
import MyCarousel from '../Components/MyCarousel'
import AddBook from '../Components/AddBook'
import Drawer from '../Components/Drawer';
//import Axios from 'axios';
import './styles/Home.css'
//import BookData from '../Asset/dataDumy/BookData';

class Home extends Component {
    constructor(props){
        super(props);
        this.state={
            //books: BookData,
            data:[{}] ,
            tempData:[{
                title:'',
                author:'',
                year:'',
                genre:'',
                descript:'',
                status:'',
                url_image:'',
            }]
        };
    // this.onClick = this.onClick.bind(this);
    };

    async componentDidMount() {
        console.log('now did mount')
        await this.props.dispatch (getBooks());
        //console.log('DISINI this.props.getData.bookData: ',this.props.getData.bookData)
        this.setState({
            data: this.props.getData.bookData
        })

         M.AutoInit()
    };

    onChangeHandle= (event) => {
        this.setState({
            tempData: {
                ...this.state.tempData,
                [event.target.name]: event.target.value
            }
        });
        // console.log('ini isi temp data title :',this.state.tempData.title)
    };

    onSubmitHandle = (event) => {
        event.preventDefault();
        const newBook = {
          title: this.state.tempData.title,
          author:  this.state.tempData.author,
          year:  this.state.tempData.year,
          genre:  this.state.tempData.genre,
        //   description:  this.state.tempData.descript,
          status: this.state.tempData.status,
          url_image: this.state.tempData.url_image,
        }


        const addData = async (newData) => {
           await this.props.dispatch(insertBook(newData))
           .then(() => {
                window.location.href='/'
            })
        }
        addData(newBook)
        //console.log('newbook: ',newBook) //check isi newBook data dari add modal
    }

// start render *************************************************************************************************    
    render() {
        console.log ('check this.state.data after render', this.state.data);
        // console.log ('check this.state.data.id after render', this.state.data[0].id);


 // return here ************************************************************************************************      
        return (
            <div className="allbody">

{/* start navbar-------------------------------------------------------------------------------------------- */}
            <nav style={{
                backgroundColor:'white',
                position:'fixed',
                zIndex:9,
            }}> 
            <a href="#!"  className="">
            <i data-target="slide-out" 
            className="material-icons sidenav-trigger black-text">menu</i></a>

            <div className="nav-wrapper container">
                <ul className="left">
                    <li>
                        {/* Dropdown Trigger
                        <a className="dropdown-trigger" 
                        href="http://localhost:3000/" 
                        data-target="dropdown1"
                        ><span style={{
                            color:'#2c3e50'
                        }}>All Categories</span>
                    </a>

                        <ul id="dropdown1" className="dropdown-content">
                            <li><a href="http://localhost:3000/">Fantasy</a></li>
                            <li><a href="http://localhost:3000/">Medicine</a></li>
                            <li><a href="http://localhost:3000/">History</a></li>
                            <li><a href="http://localhost:3000/">Religious</a></li>
                            <li><a href="http://localhost:3000/">Biography</a></li>
                        </ul> */}
                        <div class="input-field col s12">
                            <select>
                            <option value="" disabled selected>All Categories</option>
                            <option value="1">Fantasy</option>
                            <option value="2">Biography</option>
                            <option value="3">History</option>
                            <option value="4">Medicine</option>
                            <option value="5">Religious</option>
                            <option value="6">Arts</option>
                            </select>
                        </div>
                    </li>


                    <li>

                    </li>
                    <li><i className="material-icons prefix black-text">arrow_drop_down</i></li>


                    <li><i className="material-icons prefix black-text" htmlFor="icon_search"
                        style={{ 
                            paddingLeft: '3rem',
                             }}>search</i></li>
                    <li><input placeholder="Search book" id="icon_search" type="search" className="validate" /></li>


                </ul>

                <ul id="nav-mobile" className="right hide-on-med-and-down">
                    <li><img  src={book} style={{
                        width: '30px',
                        paddingTop: '1rem',
                        marginRight: '1rem',
                        }} alt="my book icon"/></li>
                    <li><span style={{
                        fontWeight:'bold',
                        color:'#2c3e50',
                        fontSize:'30px'
                    }}>Library</span></li>

                </ul>
            </div>
            </nav>

{/* end navbar-------------------------------------------------------------------------------------------- */}

            <Drawer/>
            <AddBook
                title={this.state.tempData.title}
                author={this.state.tempData.author}
                year={this.state.tempData.year}
                genre={this.state.tempData.genre}
                descript={this.state.tempData.descript}
                status={this.state.tempData.status}
                url_image={this.state.tempData.url_image}

                onChange={this.onChangeHandle}
                onSubmit={this.onSubmitHandle.bind(this)}
            />

           <div className="carousel">
            {
                this.state.data.map((book, index)=>{
                    return(
                        <MyCarousel
                        // alt={book.title.trim()}
                            key={index}
                            author={book.author}
                            cardTitle={book.title}
                            imageUrl={book.url_image}
                        />
                    );
                })
            }

           </div>

            <div className="container">
                <div className="row">
                {
                    this.state.data.map((book, index)=>{
                        return(
                            <TheCard
                            to={{
                            pathname: `book/${this.state.data[index].id}`,
                            state: {
                                books: this.state.data,
                                // isEdit: false,
                                // isDelete: false
                            }
                            }}
                            // alt={book.title.trim()}
                            key={index}
                            bookTitle={book.title}
                            image={book.url_image}
                            descript={book.descript}
                            />
                        );
                    })
                    }
                </div>
                <ul className="pagination center-align">
                    <li className=""><a href="#!"><i class="material-icons">chevron_left</i></a></li>
                    <li className="active"><a href="#!">1</a></li>
                    <li className="waves-effect"><a href="#!">2</a></li>
                    <li className="waves-effect"><a href="#!">3</a></li>
                    <li className="waves-effect"><a href="#!">4</a></li>
                    <li className="waves-effect"><a href="#!">5</a></li>
                    <li className="waves-effect"><a href="#!"><i class="material-icons">chevron_right</i></a></li>
                </ul>
            </div>

            </div>


        )
    }
}

const mapStateToProps = (state) => {
    return {
        getData: state.getBooks,
        addBook: state.insertBook,
    }
};
export default connect (mapStateToProps) (Home);
