import React, { Component } from 'react';
import 'materialize-css/dist/css/materialize.min.css';
import M from "materialize-css";

import EditBook from '../Components/EditBook'
import DelBook from '../Components/DelBook'

import pointingToLeft from '../Asset/Icons/pointing-to-left.svg'
import editIcon from '../Asset/Icons/edit-icon.svg';
import delIcon from '../Asset/Icons/delete-icon.svg';

// import coverHujan from '../Asset/bookCover/img-6hujan.jpg';

import BookData from '../Asset/dataDumy/BookData';

class BookDetail extends Component{

    constructor(){
      super();
      this.state={
        book: [],
        id:0,
        detail: {
          title:'',
          author:'',
          year:'',
          category:'',
          status:'',
          icon:'',
          img:'',
          tinydesc:'',
          fulldesc:'',
        },
        isEdit: false,
        isDelete: false,

      }
    }

    componentDidMount() {
        //M.updateTextFields();
    M.AutoInit();

    console.log('this props match params',this.props.match.params)
    if (this.props.match.params){
      const {idbook} = this.match.params;
      const {book, isEdit} = this.props.location.state;
      console.log({book: book});

      this.setState({
          book,
          id: idbook,
          detail: BookData[idbook],
          isEdit
        });
      }     
    }

    handleChange= (event) => {
      const {name, value } = event.target;

      this.setState({
        detail: {...this.state.detail,
        [name]: value 
        }
      })
    }

    onClick = (event) => {
      event.preventDefault();
      const {
        title,
        author,
        year,
        category,
        status,
        icon,
        img,
        tinydesc,
        fulldesc,
      } = this.state.detail;

      const newBook = {
        title,
        author,
        year,
        category,
        status,
        icon,
        img,
        tinydesc,
        fulldesc,
      };

      const {idbook} = this.props.match.params;
      const tempArray = this.state.book.slice;
      tempArray[idbook] = newBook;

      this.setState({
        book: tempArray,
        isEdit: true
      })
    }


    render(){

      let btnStatus=''
      if (this.state.book.status !== 'available'){
          btnStatus= 'disabled'
      }
      console.log(this.state)
     // console.log('disini isi fulldesc: ',this.state.title)
      console.log('disini nilai id params : ', this.props)



        return(
            <>
        <div>
            <div style={{
                zIndex:9,
                position:'fixed',
                margin:'0 auto',
            }} >
            <div className="row">

          <a href="#!" className="waves-effect waves-light" style={{
            marginLeft:'3rem',
            marginTop:'30px'
          }} >
             <img  src={pointingToLeft} style={{width: '40px'}} alt="arrow pointing left icon"/></a>

  <EditBook 
    url={this.state.book.img}
    title={this.state.book.title}
    descript={this.state.book.tinydesc}

  />


          <a href="#modal2" className="waves-effect waves-light modal-trigger" style={{
            marginLeft:'65rem',
            marginTop:'30px'
          }} >
             <img   src={editIcon} style={{
               width: '90px',
               }} alt="this use to edit system"/></a>

  <DelBook
  idbook={this.props.match.params.idbook}
  />
          <a href="#modal3" className="waves-effect waves-light modal-trigger" style={{
            marginLeft:'1rem',
            marginTop:'30px',
          }} >
             <img  src={delIcon} style={{width: '90px'}} alt="this use to edit system"/></a>


        </div>
            </div>

        <div className="parallax-container">
          <div className="parallax"><img src={this.state.book.img} alt="gambar dari paralax satu"/></div>
        </div>
        <div className="section white">
          <div className="row container">

              <div className="col s8">

              <div className="row">
              <div className="col s8">
                  <span className="new badge" data-badge-caption="" style={{
                    color:'black',
                    fontWeight:'bold',
                    backgroundColor:'#f1c40f',
                    marginRight:'85%'
                  }} >{this.state.book.category}</span>

                  <h2 className="header">{this.state.book.title}</h2>
                  <p>12 April 2019</p>
              </div>
              <div className="col s4">
              <h5 className="right-align" style={{
                color:'#f39c12',
                fontWeight:'bold',
                marginTop:'3rem',
              }} >{this.state.book.status}</h5>
              </div>
              </div>



              <p className="grey-text text-darken-3 lighten-3 "style={{
                textAlign:'justify'
              }} >{this.state.book.fulldesc}</p>


              </div>
              <div className="col s4">

              <img className="z-depth-3" src={this.state.book.icon} style={{
                width: '135px',
                borderRadius:'10px',
                marginLeft:'78%',
                marginTop:'-40%',
              }} alt="badge book icon"/>

              <button className={`btn ${btnStatus} waves-effect waves-light z-depth-2`}
              type="submit" style={{
              marginLeft:'90%',
              backgroundColor:'#f1c40f',
              color:'#2c3e50',
              marginTop:'70%'
              }} >Borrow
              </button>
              </div>



          </div>
        </div>
      </div>

            </>
        );
    }
}
export default BookDetail;
