import React from 'react'

const AddBook = (props) =>{


    return(
        <div id="modal1" className="modal">

        <div className="row" style={{
          marginTop:'1rem',
          marginBottom: '4rem'
        }} >
          <div className="col s6">
          <h4><span style={{
            fontWeight:'bold',
            marginLeft:'40px'
          }} >Add Data</span></h4>
          </div>
          <div className="col s6 right-align">
         <a href="#!"><i className="modal-close material-icons prefix red-text">close</i></a>
          </div>
        </div>
        <div className="row">
        <form onSubmit={props.onSubmit} className="col s12">
          <div className="row">
            <div  className="input-field col s12">
              {/* <i className="material-icons prefix">link</i> */}
              <input placeholder="url image" name='url_image' type='text' value={props.img} onChange={props.onChange} 
               id="url_image" className="materialize-textarea validate" defaultValue={""} />

            </div>

            <div className="input-field col s12">
              {/* <i className="material-icons prefix">short_text</i> */}
              <input placeholder="title" name='title' type='text' value={props.title} onChange={props.onChange} 
              id="title" className="materialize-textarea" defaultValue={""} />

            </div>

            <div className="input-field col s12">
              {/* <i className="material-icons prefix">list</i> */}
              <input placeholder="description" name='descript' type='text' value={props.tinydesc} onChange={props.onChange} 
              id="descript" className="materialize-textarea" defaultValue={""} />

            </div>

            <div className="input-field col s12">
              {/* <i className="material-icons prefix">list</i> */}
              <input placeholder="author" name='author' type='text' value={props.tinydesc} onChange={props.onChange} 
              id="author" className="materialize-textarea" defaultValue={""} />

            </div>

            <div className="input-field col s12">
              {/* <i className="material-icons prefix">list</i> */}
              <input placeholder="year" name='year' type='text' value={props.tinydesc} onChange={props.onChange} 
              id="year" className="materialize-textarea" defaultValue={""} />

            </div>

            <div className="input-field col s12">
              {/* <i className="material-icons prefix">list</i> */}
              <input placeholder="genre" name='genre' type='text' value={props.tinydesc} onChange={props.onChange} 
              id="genre" className="materialize-textarea" defaultValue={""} />

            </div>

            <div className="input-field col s12">
              {/* <i className="material-icons prefix">list</i> */}
              <input placeholder="status" name='status' type='text' value={props.tinydesc} onChange={props.onChange} 
              id="status" className="materialize-textarea" defaultValue={""} />

            </div>

            <div className="input-field col s12">
            <select>
              <option value="" disabled selected>Choose your option</option>
              <option value="1">Option 1</option>
              <option value="2">Option 2</option>
              <option value="3">Option 3</option>
            </select>
            <label>Materialize Select</label>

            </div>
          </div>

          <  button className="modal-close btn waves-effect waves-light" type="submit" name="" style={{
              marginLeft:'90%',
              backgroundColor:'#f1c40f',
              color:'#2c3e50',
              marginTop:'-2rem'
          }} >Save
          </button>
        </form>
      </div>
      </div>
    );
}
export default AddBook;
