import React, { Component } from 'react';
import 'materialize-css/dist/css/materialize.min.css';
import book from '../Asset/Icons/book.svg'
import M from "materialize-css";

import TheCard from '../Components/TheCard'
import MyCarousel from '../Components/MyCarousel'
import AddBook from '../Components/AddBook'
import Drawer from '../Components/Drawer';
import Axios from 'axios';

import './styles/Home.css'
import BookData from '../Asset/dataDumy/BookData';

class Home extends Component {
    constructor(props){
        super(props);
        this.state={
            books: BookData,
            data: [{}] 
        };
    // this.onClick = this.onClick.bind(this);
    }

    componentDidMount() {
        console.log('now did mount')
        Axios.get('http://localhost:7777/api/v1/books')
         .then(resolve => {
             console.log('resolve dot data dot data', resolve.data.data);
             console.log('resolve dot data: ', resolve.data);
             this.setState({
                 data: resolve.data.data
             })
         })
         .catch (error => {
             console.log('error', error);
         })

         M.AutoInit()
    };


// start render *************************************************************************************************    
    render() {
        console.log ('check this.data after render', this.state.data);
        console.log('ISI data url: ', this.state.data[0].url_image)

 // return here ************************************************************************************************      
        return (
            <div className="allbody">

{/* start navbar-------------------------------------------------------------------------------------------- */}
            <nav style={{
                backgroundColor:'white',
                position:'fixed',
                zIndex:9,
            }}> 
            <a href="#!"  className="">
            <i data-target="slide-out" 
            className="material-icons sidenav-trigger black-text">menu</i></a>

            <div className="nav-wrapper container">
                <ul className="left">
                    <li>
                        {/* Dropdown Trigger */}
                        <a className="dropdown-trigger" 
                        href="http://localhost:3000/" 
                        data-target="dropdown1"
                        ><span style={{
                            color:'#2c3e50'
                        }}>All Categories</span>
                    </a>
                        {/* Dropdown Structure */}
                        <ul id="dropdown1" className="dropdown-content">
                            <li><a href="http://localhost:3000/">Fantasy</a></li>
                            <li><a href="http://localhost:3000/">Medicine</a></li>
                            <li><a href="http://localhost:3000/">History</a></li>
                            <li><a href="http://localhost:3000/">Religious</a></li>
                            <li><a href="http://localhost:3000/">Biography</a></li>
                        </ul>
                    </li>
                    <li><i className="material-icons prefix black-text">arrow_drop_down</i></li>

                    <li>
                        {/* Dropdown Trigger */}
                        <a className="dropdown-trigger" 
                        href="http://localhost:3000/" 
                        data-target="dropdown2"
                        ><span style={{
                            color:'#2c3e50'
                        }}>All Times</span>
                    </a>
                        {/* Dropdown Structure */}
                        <ul id="dropdown2" className="dropdown-content">
                            <li><a href="#!">2019</a></li>
                            <li><a href="#!">2018</a></li>
                            <li><a href="#!">2017</a></li>
                            <li><a href="#!">2016</a></li>
                            <li><a href="#!">2015</a></li>
                        </ul>
                    </li>
                    <li><i className="material-icons prefix black-text">arrow_drop_down</i></li>


                    <li><i className="material-icons prefix black-text" htmlFor="icon_search"
                        style={{ 
                            paddingLeft: '3rem',
                             }}>search</i></li>
                    <li><input placeholder="Search book" id="icon_search" type="search" className="validate" /></li>


                </ul>

                <ul id="nav-mobile" className="right hide-on-med-and-down">
                    <li><img  src={book} style={{
                        width: '30px',
                        paddingTop: '1rem',
                        marginRight: '1rem',
                        }} alt="my book icon"/></li>
                    <li><span style={{
                        fontWeight:'bold',
                        color:'#2c3e50',
                        fontSize:'30px'
                    }}>Library</span></li>

                </ul>
            </div>
            </nav>

{/* end navbar-------------------------------------------------------------------------------------------- */}

            <Drawer/>
            <AddBook

            />

           <div className="carousel">
            {
                this.state.data.map((book, index)=>{
                    return(

                        <MyCarousel
                        // alt={book.title.trim()}
                            key={index}
                            author={book.author}
                            cardTitle={book.title}
                            imageUrl={book.url_image}

                        />
                    );
                })
            }

                    {
                    this.state.data.map((book, index)=>{
                        return(
                            <MyCarousel

                            // // alt={book.title.trim()}
                            // key={index}
                            // bookTitle={book.title}
                            // image={book.url_image}
                            // descript={book.descript}

                            />
                        );
                    })
                    }


           </div>

            <div className="container">
                <div className="row">
                {
                    this.state.data.map((book, index)=>{
                        return(
                            <TheCard
                            to={{
                            pathname: `book/${index}`,
                            state: {
                                books: this.state.data,
                                isEdit: false,
                                isDelete: false
                            }
                            }}
                            // alt={book.title.trim()}
                            key={index}
                            bookTitle={book.title}
                            image={book.url_image}
                            descript={book.descript}

                            />
                        );
                    })
                    }
                </div>
            </div>
            </div>


        )
    }
}

export default Home;
